// src/pages/Home.js
import React, { useState } from 'react';
import SearchForm from '../components/SearchForm';  // Make sure the path is correct

const Home = () => {
  const [movies, setMovies] = useState([]);  // State to hold the search results

  const handleSearchResults = (results) => {
    console.log("Search results received:", results); // Debugging: Log the results to ensure it's working
    setMovies(results);  // Update the state with the search results
  };

  return (
    <div>
      <h1>Movie Search</h1>
      <SearchForm onSearchResults={handleSearchResults} />  {/* Pass the handler to SearchForm */}

      <div>
        <h2>Search Results:</h2>
        {movies.length > 0 ? (
          <ul>
            {movies.map((movie) => (
              <li key={movie._id}>
                <h3>{movie.title}</h3>  {/* Display movie title */}
                <p>Genre: {movie.genre}</p>  {/* Display movie genre */}
                <p>Year: {movie.year}</p>  {/* Display movie year */}
              </li>
            ))}
          </ul>
        ) : (
          <p>No results found</p>  
        )}
      </div>
    </div>
  );
};

export default Home;
