import React, { useState, useEffect } from 'react';
import { fetchMovies, searchMovies } from '../api';
import './Home.css';

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [query, setQuery] = useState('');

  useEffect(() => {
    const loadMovies = async () => {
      const data = await fetchMovies();
      setMovies(data);
    };
    loadMovies();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    const results = await searchMovies(query);
    setMovies(results);
  };

  return (
    <div className="netflix-home">
      {/* Hero Banner */}
      <div className="hero-banner">
        <div className="hero-content">
          <h1 className="hero-title">Unlimited movies, TV shows, and more.</h1>
          <form onSubmit={handleSearch} className="search-form">
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Search for movies, TV shows..."
              className="search-input"
            />
            <button type="submit" className="search-button">
              Search
            </button>
          </form>
        </div>
      </div>

      {/* Movie Rows */}
      <div className="content">
        <div className="movie-row">
          <h2 className="row-title">Trending Now</h2>
          <div className="row-posters">
            {movies.map((movie) => (
              <div key={movie._id} className="movie-card">
                <img
                  src={movie.poster || 'https://via.placeholder.com/300x450'}
                  alt={movie.title}
                  className="movie-poster"
                />
                <div className="movie-overlay">
                  <h3>{movie.title}</h3>
                  <p>{movie.genre} â€¢ {movie.year}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Additional rows would go here */}
        <div className="movie-row">
          <h2 className="row-title">Popular on Netflix</h2>
          <div className="row-posters">
            {movies.slice().reverse().map((movie) => (
              <div key={`popular-${movie._id}`} className="movie-card">
                <img
                  src={movie.poster || 'https://via.placeholder.com/300x450'}
                  alt={movie.title}
                  className="movie-poster"
                />
                <div className="movie-overlay">
                  <h3>{movie.title}</h3>
                  <p>{movie.genre} â€¢ {movie.year}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
