// src/App.js

import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import MovieDetails from './pages/MovieDetails';
import NotFound from './pages/NotFound';
import Navbar from './components/Navbar';  // Assuming a Navbar component exists for navigation

const App = () => {
  return (
    <Router>
      {/* Render a Navbar on all pages */}
      <Navbar />
      <div className="app-container">
        <Routes>
          <Route path="/" element={<Home />} />  {/* Home page */}
          <Route path="/login" element={<Login />} />  {/* Login page */}
          <Route path="/register" element={<Register />} />  {/* Register page */}
          <Route path="/movie/:id" element={<MovieDetails />} />  {/* Movie details page */}
          <Route path="*" element={<NotFound />} />  {/* 404 Not Found */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
