// src/components/SearchForm.js

import React, { useState } from 'react';
import { searchMovies } from '../api';  // Import the searchMovies function from api.js

const SearchForm = ({ onSearchResults }) => {
  const [query, setQuery] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Call the searchMovies function from api.js
      const searchResults = await searchMovies(query);
      onSearchResults(searchResults);  // Trigger the parent component's search results handler
    } catch (error) {
      console.error('Search failed:', error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for movies"
      />
      <button type="submit">Search</button>
    </form>
  );
};

export default SearchForm;
