// src/pages/Home.js

import React, { useState, useEffect } from 'react';
import MovieList from '../components/MovieList';
import SearchForm from '../components/SearchForm';
import { fetchMovies } from '../api';
import './Home.css'; // Retain your styles

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  // Fetch all movies on component mount
  useEffect(() => {
    const getMovies = async () => {
      try {
        const movieData = await fetchMovies();
        setMovies(movieData);
      } catch (error) {
        console.error('Error fetching movies:', error.message);
      }
    };

    getMovies();
  }, []);

  // Update search results
  const handleSearchResults = (results) => {
    setSearchResults(results);
  };

  return (
    <div className="home-container">
      <header className="home-header">
        <h1>Welcome to Netflix Clone</h1>
        <SearchForm onSearchResults={handleSearchResults} />
      </header>
      <section className="movie-section">
        <h2>Movies</h2>
        {/* Display search results if available; otherwise, show all movies */}
        <MovieList movies={searchResults.length > 0 ? searchResults : movies} />
      </section>
    </div>
  );
};

export default Home;
