// src/api.js

import axios from 'axios';

// Fetch all movies
export const fetchMovies = async () => {
  try {
    const response = await axios.get('http://localhost:5000/api/movies'); // Adjust with your API URL
    return response.data;
  } catch (error) {
    console.error("Error fetching movies", error);
    throw error;
  }
};

// Search movies by title
export const searchMovies = async (query) => {
  try {
    const response = await axios.get(`http://localhost:5000/api/movies/search?query=${query}`);
    return response.data;
  } catch (error) {
    console.error("Error searching movies", error);
    throw error;
  }
};

// Fetch a single movie by ID
export const fetchMovieById = async (id) => {
  try {
    const response = await axios.get(`http://localhost:5000/api/movies/${id}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching movie by ID", error);
    throw error;
  }
};

// Example of other API functions like user login, registration, etc.
export const loginUser = async (credentials) => {
  try {
    const response = await axios.post('http://localhost:5000/api/auth/login', credentials);
    return response.data;
  } catch (error) {
    console.error("Error logging in", error);
    throw error;
  }
};

