import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieCard from '../components/MovieCard';
import SearchBar from '../components/SearchBar';
import './HomePage.css';

const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [featuredMovie, setFeaturedMovie] = useState(null);
  const [movieCategories, setMovieCategories] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch featured content
        const featuredResponse = await axios.get('http://localhost:5000/api/movies/featured');
        setFeaturedMovie(featuredResponse.data);
        
        // Fetch movies by categories
        const categoriesResponse = await axios.get('http://localhost:5000/api/movies/categories');
        setMovieCategories(categoriesResponse.data);
        
        // Fetch all movies for search functionality
        const moviesResponse = await axios.get('http://localhost:5000/api/movies');
        setMovies(moviesResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;
    
    try {
      const response = await axios.get(`http://localhost:5000/api/movies/search?query=${searchQuery}`);
      setMovies(response.data);
      setMovieCategories([]); // Clear categories when showing search results
    } catch (error) {
      console.error('Error searching movies:', error);
    }
  };

  const clearSearch = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/movies');
      setMovies(response.data);
      setSearchQuery('');
      
      // Re-fetch categories
      const categoriesResponse = await axios.get('http://localhost:5000/api/movies/categories');
      setMovieCategories(categoriesResponse.data);
    } catch (error) {
      console.error('Error clearing search:', error);
    }
  };

  return (
    <div className="netflix-homepage">
      {/* Featured Banner */}
      {featuredMovie && !searchQuery && (
        <div className="featured-banner" style={{
          backgroundImage: `linear-gradient(to top, rgba(0, 0, 0, 0.8) 0%, rgba(0, 0, 0, 0) 100%), url(${featuredMovie.backdropUrl})`
        }}>
          <div className="featured-content">
            <h1 className="featured-title">{featuredMovie.title}</h1>
            <p className="featured-description">{featuredMovie.description}</p>
            <div className="featured-buttons">
              <button className="play-button">
                <i className="fas fa-play"></i> Play
              </button>
              <button className="info-button">
                <i className="fas fa-info-circle"></i> More Info
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Search Results or Categories */}
      <div className="content-container">
        <SearchBar
          searchQuery={searchQuery}
          setSearchQuery={setSearchQuery}
          handleSearch={handleSearch}
          clearSearch={clearSearch}
        />

        {searchQuery ? (
          <div className="search-results">
            <h2 className="section-title">Search Results for "{searchQuery}"</h2>
            <div className="movie-grid">
              {movies.length ? (
                movies.map((movie) => <MovieCard key={movie.id} movie={movie} />)
              ) : (
                <p className="no-results">No movies found.</p>
              )}
            </div>
          </div>
        ) : (
          movieCategories.map((category) => (
            <div key={category.name} className="movie-row">
              <h2 className="section-title">{category.name}</h2>
              <div className="movie-slider">
                {category.movies.map((movie) => (
                  <MovieCard key={movie.id} movie={movie} />
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default HomePage;
