import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import SearchForm from './SearchForm';
import MovieList from './MovieList';
import './Home.css';

const API_KEY = 'YOUR_TMDB_API_KEY';

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);

  useEffect(() => {
    const fetchTrending = async () => {
      const response = await axios.get(
        `https://api.themoviedb.org/3/trending/all/week?api_key=${API_KEY}`
      );
      setMovies(response.data.results);
    };
    fetchTrending();
  }, []);

  const handleSearch = async (query) => {
    if (!query.trim()) {
      setIsSearching(false);
      return;
    }
    
    setIsSearching(true);
    const response = await axios.get(
      `https://api.themoviedb.org/3/search/multi?api_key=${API_KEY}&query=${query}`
    );
    setSearchResults(response.data.results);
  };

  return (
    <div className="home">
      <Navbar />
      <div className="main-content">
        <SearchForm onSearch={handleSearch} />
        
        {isSearching ? (
          <MovieList title="Search Results" movies={searchResults} />
        ) : (
          <>
            <MovieList title="Trending Now" movies={movies} />
            <MovieList title="Popular on Netflix" movies={movies.slice().reverse()} />
            <MovieList title="Watch Again" movies={movies.slice(0, 10)} />
          </>
        )}
      </div>
    </div>
  );
};

export default Home;
