import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });
      
      if (response.data.success) {
        localStorage.setItem('netflix_user', JSON.stringify(response.data.user));
        localStorage.setItem('netflix_token', response.data.token);
        navigate('/');
      } else {
        setError(response.data.message || 'Invalid email or password');
      }
    } catch (error) {
      setError(error.response?.data?.message || 'Login failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <nav className="login-nav">
        <Link to="/" className="netflix-logo">
          <svg viewBox="0 0 111 30" className="netflix-svg">
            <g id="netflix-logo">
              <path d="M105.06233,14.2806261 L110.999156,30 C109.249227,29.7497422 107.500234,29.4366857 105.718437,29.1554972 L102.374168,20.4686475 L98.9371075,28.4375293 C97.2499766,28.1563408 95.5928391,28.061674 93.9057081,27.8432843 L99.9372012,14.0931671 L94.4680851,-5.68434189e-14 L99.5313525,-5.68434189e-14 L102.593495,7.87421502 L105.874965,-5.68434189e-14 L110.999156,-5.68434189e-14 L105.06233,14.2806261 Z" fill="#e50914"></path>
            </g>
          </svg>
        </Link>
      </nav>
      
      <div className="login-content">
        <div className="login-wrapper">
          <h1 className="login-title">Sign In</h1>
          
          {error && <div className="login-error">{error}</div>}
          
          <form onSubmit={handleLogin} className="login-form">
            <div className="form-group">
              <input
                type="email"
                placeholder="Email or phone number"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            
            <div className="form-group">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            
            <button 
              type="submit" 
              className="login-button"
              disabled={isLoading}
            >
              {isLoading ? 'Signing In...' : 'Sign In'}
            </button>
            
            <div className="login-help">
              <div className="remember-me">
                <input type="checkbox" id="remember" />
                <label htmlFor="remember">Remember me</label>
              </div>
              <Link to="/help" className="help-link">Need help?</Link>
            </div>
          </form>
          
          <div className="login-footer">
            <p className="new-to-netflix">
              New to Netflix? <Link to="/signup" className="signup-link">Sign up now</Link>.
            </p>
            <p className="recaptcha-notice">
              This page is protected by Google reCAPTCHA to ensure you're not a bot. <button className="learn-more">Learn more</button>.
            </p>
          </div>
        </div>
      </div>
      
      <footer className="login-footer-bottom">
        <div className="footer-content">
          <p>Questions? Call 1-844-505-2993</p>
          <div className="footer-links">
            <Link to="/help">FAQ</Link>
            <Link to="/privacy">Privacy</Link>
            <Link to="/cookies">Cookie Preferences</Link>
            <Link to="/corporate">Corporate Information</Link>
          </div>
          <div className="language-selector">
            <select>
              <option value="en">English</option>
              <option value="es">Espa√±ol</option>
            </select>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default LoginPage;
