// src/pages/MovieDetails.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { fetchMovieById } from '../api';
import './MovieDetails.css';  // Import the CSS file

const MovieDetails = () => {
  const { id } = useParams();  // Get the movie ID from URL params
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);  // Track loading state
  const [error, setError] = useState(null);  // Track error state

  useEffect(() => {
    const getMovieDetails = async () => {
      try {
        setLoading(true);  // Set loading to true before fetching
        setError(null);    // Reset error state before fetching
        const movieData = await fetchMovieById(id);
        if (movieData) {
          setMovie(movieData);  // Set the movie data if found
        } else {
          setError('Movie not found');  // Handle case where no movie is found
        }
      } catch (error) {
        setError('Error fetching movie details');  // Set error message in case of fetch failure
      } finally {
        setLoading(false);  // Set loading to false after fetch completion
      }
    };

    getMovieDetails();  // Fetch movie details on component mount
  }, [id]);

  if (loading) return <p>Loading...</p>;  // Show loading text while data is being fetched
  if (error) return <p>{error}</p>;  // Show error message if something goes wrong

  return (
    <div className="movie-details-container">
      <div className="movie-details-content">
        <div className="movie-poster">
          <img src={movie.posterUrl} alt={movie.title} />
        </div>
        <div className="movie-info">
          <h1>{movie.title}</h1>
          <p className="movie-genre">Genre: {movie.genre}</p>
          <p className="movie-year">Year: {movie.year}</p>
          <p className="movie-description">{movie.description}</p>
        </div>
      </div>
    </div>
  );
};

export default MovieDetails;
